import React, { useState } from 'react';
import { Stepper, Step, Button } from '@material-tailwind/react';

export default function Donate() {
  const [activeStep, setActiveStep] = useState(0);
  const [isLastStep, setIsLastStep] = useState(false);
  const [isFirstStep, setIsFirstStep] = useState(false);
  const [nextClicks, setNextClicks] = useState(0);
  const [formData, setFormData] = useState({
    fullname: '',
    studentNumber: '',
    email: '',
  });
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleNext = () => {
    if (activeStep < 2 && nextClicks < 2) {
      setActiveStep((cur) => cur + 1);
      setNextClicks((count) => count + 1);
    } else {
      // Handle maximum number of next clicks reached
      console.log('Maximum number of next clicks reached');
    }
  };

  const handlePrev = () => {
    setActiveStep((cur) => cur - 1);
    setNextClicks(0);
  };

  React.useEffect(() => {
    setIsFirstStep(activeStep === 0);
    setIsLastStep(activeStep === 2);
  }, [activeStep]);

  const renderStepContent = (step) => {
    switch (step) {
      case 0:
        return (
          <div>
            <div className="flex flex-wrap">
              <div className="w-full md:w-1/2 px-2">
                <div className="bg-white rounded-md shadow-md p-4 mb-4">
                  <h3 className="text-lg font-medium mb-2">Select a Donation Amount</h3>
                  <div className="flex flex-wrap">
                    <button className="bg-blue-500 text-white px-4 py-2 rounded-md mr-2 mb-2">
                      $10
                    </button>
                    <button className="bg-blue-500 text-white px-4 py-2 rounded-md mr-2 mb-2">
                      $20
                    </button>
                    <button className="bg-blue-500 text-white px-4 py-2 rounded-md mr-2 mb-2">
                      $50
                    </button>
                  </div>
                  <input
                    type="text"
                    className="border border-gray-300 rounded-md px-3 py-2 mt-4 w-full"
                    placeholder="Enter Custom Amount"
                  />
                </div>
              </div>
              <div className="w-full md:w-1/2 px-2">
                <div className="bg-white rounded-md shadow-md p-4 mb-4">
                  <h3 className="text-lg font-medium mb-2">Other Details</h3>
                  <label htmlFor="title" className="block text-sm font-medium text-gray-900">
                    Title
                  </label>
                  <input
                    type="text"
                    id="title"
                    className="border border-gray-300 rounded-md px-3 py-2 mt-1 w-full"
                  />
                  <label htmlFor="category" className="block text-sm font-medium text-gray-900 mt-4">
                    Category
                  </label>
                  <select
                    id="category"
                    className="border border-gray-300 rounded-md px-3 py-2 mt-1 w-full"
                  >
                    <option value="">Select Category</option>
                    <option value="category1">Category 1</option>
                    <option value="category2">Category 2</option>
                    <option value="category3">Category 3</option>
                  </select>
                </div>
              </div>
            </div>
          </div>
        );
      case 1:
        return (
          <div>
            <div className="flex flex-wrap">
              <div className="w-full md:w-1/2 px-2">
                <div className="bg-white rounded-md shadow-md p-4 mb-4">
                  <h3 className="text-lg font-medium mb-2">Personal Information</h3>
                  <label htmlFor="fullname" className="block text-sm font-medium text-gray-900">
                    Full Name
                  </label>
                  <input
                    type="text"
                    id="fullname"
                    className="border border-gray-300 rounded-md px-3 py-2 mt-1 w-full"
                    value={formData.fullname}
                    onChange={(e) => setFormData({ ...formData, fullname: e.target.value })}
                  />
                  <label htmlFor="studentNumber" className="block text-sm font-medium text-gray-900 mt-4">
                    Student Number
                  </label>
                  <input
                    type="text"
                    id="studentNumber"
                    className="border border-gray-300 rounded-md px-3 py-2 mt-1 w-full"
                    value={formData.studentNumber}
                    onChange={(e) => setFormData({ ...formData, studentNumber: e.target.value })}
                  />
                </div>
              </div>
              <div className="w-full md:w-1/2 px-2">
                <div className="bg-white rounded-md shadow-md p-4 mb-4">
                  <h3 className="text-lg font-medium mb-2">Contact Information</h3>
                  <label htmlFor="email" className="block text-sm font-medium text-gray-900">
                    Email Address
                  </label>
                  <input
                    type="email"
                    id="email"
                    className="border border-gray-300 rounded-md px-3 py-2 mt-1 w-full"
                    value={formData.email}
                    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  />
                  <label htmlFor="phone" className="block text-sm font-medium text-gray-900 mt-4">
                    Phone Number
                  </label>
                  <input
                    type="text"
                    id="phone"
                    className="border border-gray-300 rounded-md px-3 py-2 mt-1 w-full"
                  />
                </div>
              </div>
            </div>
          </div>
        );
      case 2:
        return (
          <div className="flex flex-wrap">
            <div className="w-full px-2">
              <div className="bg-white rounded-md shadow-md p-4">
                <h3 className="text-lg font-medium mb-2">Payment Method</h3>
                <label htmlFor="cardNumber" className="block text-sm font-medium text-gray-900">
                  Card Number
                </label>
                <input
                  type="text"
                  id="cardNumber"
                  className="border border-gray-300 rounded-md px-3 py-2 mt-1 w-full"
                />
                <label htmlFor="expiryDate" className="block text-sm font-medium text-gray-900 mt-4">
                  Expiry Date
                </label>
                <input
                  type="text"
                  id="expiryDate"
                  className="border border-gray-300 rounded-md px-3 py-2 mt-1 w-full"
                />
              </div>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  const handleModalOpen = () => {
    setIsModalOpen(true);
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="py-8">
      <Stepper activeStep={activeStep}>
        <Step step={0} label="Step 1" />
        <Step step={1} label="Step 2" />
        <Step step={2} label="Step 3" />
      </Stepper>

      <div className="my-4">{renderStepContent(activeStep)}</div>

      <div className="flex justify-between">
        {!isFirstStep && (
          <Button color="indigo" onClick={handlePrev}>
            Back
          </Button>
        )}

        {!isLastStep ? (
          <Button color="indigo" onClick={handleNext}>
            Next
          </Button>
        ) : (
          <Button color="indigo" onClick={handleModalOpen}>
            Donate
          </Button>
        )}
      </div>

      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center z-50">
          <div className="absolute inset-0 bg-gray-900 opacity-50"></div>
          <div className="bg-white w-96 rounded-lg shadow-lg p-6 z-10">
            <h3 className="text-lg font-medium mb-2">Confirmation</h3>
            <p>Are you sure you want to proceed with the donation?</p>
            <div className="mt-6 flex justify-end">
              <Button color="gray" onClick={handleModalClose}>
                Cancel
              </Button>
              <Button color="indigo" onClick={handleModalClose}>
                Confirm
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
