export default function Testimonies() {
  const [currentTestimony, setCurrentTestimony] = useState(0);
  const { t, i18n } = useTranslation();

  const nextTestimony = () => {
    setCurrentTestimony((prevTestimony) => (prevTestimony + 1) % testimonies.length);
  };

  const previousTestimony = () => {
    setCurrentTestimony((prevTestimony) => (prevTestimony - 1 + testimonies.length) % testimonies.length);
  };

  const { quote, author, role, image } = testimonies[currentTestimony];

  return (
    <section className="relative isolate overflow-hidden bg-white px-6 py-24 sm:py-32 lg:px-8">
      <div className="absolute inset-0 -z-10 bg-[radial-gradient(45rem_50rem_at_top,theme(colors.indigo.100),white)] opacity-20" />
      <div className="absolute inset-y-0 right-1/2 -z-10 mr-16 w-[200%] origin-bottom-left skew-x-[-30deg] bg-white shadow-xl shadow-indigo-600/10 ring-1 ring-indigo-50 sm:mr-28 lg:mr-0 xl:mr-16 xl:origin-center" />
      <div className="mx-auto max-w-2xl lg:max-w-4xl">
        <img className="mx-auto h-12" src="https://tailwindui.com/img/logos/workcation-logo-indigo-600.svg" alt="" />
        <figure className="mt-10 flex items-center justify-center sm:justify-start">
          <blockquote className="text-center sm:text-left text-xl font-semibold leading-8 text-gray-900 sm:text-2xl sm:leading-9">
            <p>
              {quote}
            </p>
          </blockquote>
          <div className="ml-6">
            <img className="h-10 w-10 rounded-full" src={image} alt="" />
            <div className="mt-4 flex items-center space-x-3 text-base">
              <div className="font-semibold text-gray-900">{author}</div>
              <svg viewBox="0 0 2 2" width={3} height={3} aria-hidden="true" className="fill-gray-900">
                <circle cx={1} cy={1} r={1} />
              </svg>
              <div className="text-gray-600">{role}</div>
            </div>
          </div>
        </figure>
      </div>
      <div className="mt-8 flex justify-center">
        <button
          className="mr-4 p-2 rounded-full bg-indigo-500 text-white focus:outline-none"
          onClick={previousTestimony}
        >
          &lt;
        </button>
        <button
          className="p-2 rounded-full bg-indigo-500 text-white focus:outline-none"
          onClick={nextTestimony}
        >
          &gt;
        </button>
      </div>
    </section>
  )
}
