import React from 'react';
import { useTranslation } from 'react-i18next';
import i18next from 'i18next';
import logo from './logo.png'; // Replace with the path to your logo image

function Navbar() {
  const { t, i18n } = useTranslation();

  function handleClick() {
    const lang = i18n.language === 'en' ? 'fr' : 'en';
    i18next.changeLanguage(lang);
  }

  const styles = {
    navLink: {
      textDecoration: 'none',
      color: 'black',
      fontSize: '18px',
      padding: '0 10px',
      fontWeight: 'bold',
    },
  };

  return (
    <nav style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', fontFamily: 'Plus Jakarta Sans, sans-serif' }}>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <img src={logo} alt="Logo" id="logo" style={{ width: '150px', padding: '10px', marginRight: '10px',marginLeft: '30px' }} />
      </div>
      <div style={{ display: 'flex', alignItems: 'center', marginLeft: 'auto', marginRight: '30px' }}>
        <a href="/about" id="nav-link" style={{ textDecoration: 'none', color: 'black', fontSize: '18px', padding: '0 10px', fontWeight: 'bold' }}>{t('About Us')}</a>
        <a href="/donations" id="nav-link" style={{ textDecoration: 'none', color: 'black', fontSize: '18px', padding: '0 10px', fontWeight: 'bold' }}>{t('Donations')}</a>
        <a href="/bachelors" id="nav-link" style={{ textDecoration: 'none', color: 'black', fontSize: '18px', padding: '0 10px', fontWeight: 'bold' }}>{t('Bachelors')}</a>
        <a href="/faq" id="nav-link" style={{ textDecoration: 'none', color: 'black', fontSize: '18px', padding: '0 10px', fontWeight: 'bold' }}>{t('FAQ')}</a>
        <a href="/contact" id="nav-link" style={{ textDecoration: 'none', color: 'black', fontSize: '18px', padding: '0 10px', fontWeight: 'bold' }}>{t('Contact Us')}</a>
        <button onClick={handleClick} style={{textDecoration: 'none', color: 'black', fontSize: '18px', padding: '0 10px', fontWeight: 'bold' }}>
          {i18n.language === 'en' ? 'FR' : 'EN'}
        </button>
      </div>
      <div>
        
      </div>
    </nav>
  );
}

export default Navbar;
