// Homepage.js

// Create the sections dynamically
function createSections() {
    // Get the main container
    var container = document.getElementById('container');
  
    // Create Home section
    var homeSection = document.createElement('section');
    homeSection.id = 'home';
    homeSection.textContent = 'Home Section';
    container.appendChild(homeSection);
  
    // Create About Us section
    var aboutSection = document.createElement('section');
    aboutSection.id = 'about';
    aboutSection.textContent = 'About Us Section';
    container.appendChild(aboutSection);
  
    // Create FAQ section
    var faqSection = document.createElement('section');
    faqSection.id = 'faq';
    faqSection.textContent = 'FAQ Section';
    container.appendChild(faqSection);
  
    // Create Contact Us section
    var contactSection = document.createElement('section');
    contactSection.id = 'contact';
    contactSection.textContent = 'Contact Us Section';
    container.appendChild(contactSection);
  }
  
  // Call the createSections function to add the sections
  createSections();
// CSS within the same JavaScript file

var styles = `
<style>
  body {
    font-family: 'Plus Jakarta Sans', sans-serif;
    margin: 0;
    padding: 0;
  }

  #container {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100vh;
    text-align: center;
  }

  section {
    padding: 20px;
  }

  #home {
    background-color: #f2f2f2;
  }

  #about {
    background-color: #e2e2e2;
  }

  #faq {
    background-color: #d2d2d2;
  }

  #contact {
    background-color: #c2c2c2;
  }

  @media (max-width: 600px) {
    section {
      padding: 10px;
    }
  }
</style>`;

// Add the styles to the document head
document.head.innerHTML += styles;
export default Homepage;
