import { useRef } from "react";
import { FaBars, FaTimes } from "react-icons/fa";
import "../styles.css";

function Navbar() {
	const navRef = useRef();

	const showNavbar = () => {
		navRef.current.classList.toggle(
			"responsive_nav"
		);
	};
  function handleNavLinkHover(event) {
    const link = event.target;
    const dot = link.querySelector('.dot');

    link.style.color = hoverColor; // Change text color to orange
    dot.style.visibility = 'visible';
  }

  function handleNavLinkLeave(event) {
    const link = event.target;
    const dot = link.querySelector('.dot');

    link.style.color = styles.navLink.color; // Restore original text color
    dot.style.visibility = 'hidden';
  }

	return (
		<header>
			<h3>LOGO</h3>
			<nav ref={navRef}>
				<a href="/#">Home</a>
				<a href="/#">My work</a>
				<a href="/#">Blog</a>
				<a href="/#">About me</a>
				<button
					className="nav-btn nav-close-btn"
					onClick={showNavbar}>
					<FaTimes />
				</button>
			</nav>
			<button
				className="nav-btn"
				onClick={showNavbar}>
				<FaBars />
			</button>
		</header>
	);
}

export default Navbar;
