import { useRef } from "react";
import { FaBars, FaTimes } from "react-icons/fa";
import "../styles.css";
import { useTranslation } from 'react-i18next';
import i18next from 'i18next';
import logo from './logo.png'; // Replace with the path to your logo image


function Navbar() {
  const { t, i18n } = useTranslation();

  function handleClick() {
    const lang = i18n.language === 'en' ? 'fr' : 'en';
    i18next.changeLanguage(lang);
  }


	const navRef = useRef();

	const showNavbar = () => {
		navRef.current.classList.toggle(
			"responsive_nav"
		);
	};

	return (
		<header>
		<nav ref={navRef}>
  <a href="/" style={{ textDecoration: 'none' }}>
    <img src={logo} alt="Logo" id="logo" style={{ width: '200px', padding: '10px', marginRight: '10px', marginLeft: '50px' }} />
  </a>
  <div style={{ marginLeft: 'auto' }}>
    <a> {t('About Us')} </a>
    <a> {t('Donations')} </a>
    <a> {t('Bachelors')} </a>
    <a> {t('FAQ')} </a>
    <a> {t('Contact Us')} </a>
    <button onClick={handleClick} id="translate" className="translate-button">
      {i18n.language === 'en' ? 'FR' : 'EN'}
      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-globe" viewBox="0 0 16 16">
      
      </svg>
    </button>
    <button className="nav-btn nav-close-btn" onClick={showNavbar}>
      <FaTimes />
    </button>
  </div>
</nav>

			<button
				className="nav-btn"
				onClick={showNavbar}>
				<FaBars />
			</button>
		</header>
	);
}

export default Navbar;
