import React from 'react';
import logo from './logo.svg';
import './App.css';
import { useTranslation } from 'react-i18next';
import i18next from 'i18next';

function App() {
  const { t } = useTranslation();

  function handleClick(lang) {
    i18next.changeLanguage(lang);
  }

  return (
    <div className="App">
      <nav style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', width: '100%', padding: '2rem 0', backgroundColor: 'gray' }}>
        <div>
          <a href="/about">{t('About Us')}</a>
          <a href="/donations">{t('Donations')}</a>
          <a href="/bachelors">{t('Bachelors')}</a>
          <a href="/faq">{t('FAQ')}</a>
          <a href="/contact">{t('Contact Us')}</a>
        </div>
        <div>
          <button onClick={() => handleClick('en')}>
            EN
          </button>
          <button onClick={() => handleClick('fr')}>
            FR
          </button>
        </div>
      </nav>
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          <h3>{t('Thanks.1')}</h3>
          <h3>{t('Why.1')}</h3>
        </p>
      </header>
    </div>
  );
}

export default App;
