import { useRef } from "react";
import { FaBars, FaTimes } from "react-icons/fa";
import "../styles.css";
import { useTranslation } from 'react-i18next';
import i18next from 'i18next';
import logo from './logo.png'; // Replace with the path to your logo image


function Navbar() {
  const { t, i18n } = useTranslation();

  function handleClick() {
    const lang = i18n.language === 'en' ? 'fr' : 'en';
    i18next.changeLanguage(lang);
  }


	const navRef = useRef();

	const showNavbar = () => {
		navRef.current.classList.toggle(
			"responsive_nav"
		);
	};

	return (
		<header>
			 
       <nav ref={navRef} style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
  <a href="/" style={{ textDecoration: 'none' }}>
    <img src={logo} alt="Logo" id="logo" style={{ width: '200px', padding: '10px', marginRight: '10px', marginLeft: '50px' }} />
  </a>
  <div style={{ display: 'flex', alignItems: 'center' }}>
    <a> {t('About Us')} </a>
    <a> {t('Donations')} </a>
    <a> {t('Bachelors')} </a>
    <a> {t('FAQ')} </a>
    <a> {t('Contact Us')} </a>
    <button onClick={handleClick} id="translate" className="translate-button">Translate</button>
  </div>
</nav>
			<button
				className="nav-btn"
				onClick={showNavbar}>
				<FaBars />
			</button>
		</header>
	);
}

export default Navbar;
