import React from 'react';
import logo from './logo.svg';
import './App.css';
import { useTranslation } from 'react-i18next';
import i18next from 'i18next';

function App() {
  const { t, i18n } = useTranslation();

  function handleClick() {
    const lang = i18n.language === 'en' ? 'fr' : 'en';
    i18next.changeLanguage(lang);
  }

  return (
    <div className="App">
      <nav style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', width: '100%', padding: '2rem 0', backgroundColor: 'gray' }}>
        <div>
          <a href="/about">{t('About Us')}</a>
          <a href="/donations">{t('Donations')}</a>
          <a href="/bachelors">{t('Bachelors')}</a>
          <a href="/faq">{t('FAQ')}</a>
          <a href="/contact">{t('Contact Us')}</a>
        </div>

      </nav>
      <div>
        <button onClick={handleClick}>
          {i18n.language === 'en' ? 'FR' : 'EN'}
        </button>
      </div>
      <section>
        <h2>{t('Section.Title')}</h2>
        <p>{t('Section.Content')}</p>
      </section>
    </div>


  );
}

export default App;
