import React from 'react';
import { useTranslation } from 'react-i18next';
import i18next from 'i18next';
import logo from './logo.png'; // Replace with the path to your logo image

function Navbar() {
  const { t, i18n } = useTranslation();

  function handleClick() {
    const lang = i18n.language === 'en' ? 'fr' : 'en';
    i18next.changeLanguage(lang);
  }

  const styles = {
    navLink: {
      textDecoration: 'none',
      color: 'black',
      fontSize: '18px',
      padding: '0 10px',
      fontWeight: 'bold',
    },
    
      translateButton: {
        background: 'none',
        border: 'none',
        
      },

  };

  return (
    <nav style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', fontFamily: 'Plus Jakarta Sans, sans-serif' }}>
      <div >
        <img src={logo} alt="Logo" id="logo" style={{ width: '200px', padding: '10px', marginRight: '10px',marginLeft: '50px' }} />
      </div>
      <div style={{ display: 'flex', alignItems: 'center', marginLeft: 'auto', marginRight: '50px',padding: '10px' }}>
      <a href="/about" id="nav-link" style={styles.navLink}>{t('About Us')}</a>
        <a href="/donations" id="nav-link" style={styles.navLink}>{t('Donations')}</a>
        <a href="/bachelors" id="nav-link" style={styles.navLink}>{t('Bachelors')}</a>
        <a href="/faq" id="nav-link" style={styles.navLink}>{t('FAQ')}</a>
        <a href="/contact" id="nav-link" style={styles.navLink}>{t('Contact Us')}</a>
    
        <button onClick={handleClick} id="translate" className="translate-button" style={styles.translateButton}>
  {i18n.language === 'en' ? 'FR' : 'EN'}
  <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-globe" viewBox="0 0 16 16">
    <path d="M0 8a8 8 0 1 1 16 0A8 8 0 0 1 0 8zm7.5-6.923c-.67.204-1.335.82-1.887 1.855A7.97 7.97 0 0 0 5.145 4H7.5V1.077zM4.09 4a9.267 9.267 0 0 1 .64-1.539 6.7 6.7 0 0 1 .597-.933A7.025 7.025 0 0 0 2.255 4H4.09zm-.582 3.5c.03-.877.138-1.718.312-2.5H1.674a6.958 6.958 0 0 0-.656 2.5h2.49zM4.847 5a12.5 12.5 0 0 0-.338 2.5H7.5V5H4.847zM8.5 5v2.5h2.99a12.495 12.495 0 0 0-.337-2.5H8.5zM4.51 8.5a12.5 12.5 0 0 0 .337 2.5H7.5V8.5H4.51zm3.99 0V11h2.653c.187-.765.306-1.608.338-2.5H8.5zM5.145 12c.138.386.295.744.468 1.068.552 1.035 1.218 1.65 1.887 1.855V12H5.145zm.182 2.472a6.696 6.696 0 0 1-.597-.933A9.268 9.268 0 0 1 4.09 12H2.255a7.024 7.024 0 0 0 3.072 2.472zM3.82 11a13.652 13.652 0 0 1-.312-2.5h-2.49c.062.89.291 1.733.656 2.5H3.82zm6.853 3.472A7.024 7.024 0 0 0 13.745 12H11.91a9.27 9.27 0 0 1-.64 1.539 6.688 6.688 0 0 1-.597.933zM8.5 12v2.923c.67-.204 1.335-.82 1.887-1.855.173-.324.33-.682.468-1.068H8.5zm3.68-1h2.146c.365-.767.594-1.61.656-2.5h-2.49a13.65 13.65 0 0 1-.312 2.5zm2.802-3.5a6.959 6.959 0 0 0-.656-2.5H12.18c.174.782.282 1.623.312 2.5h2.49zM11.27 2.461c.247.464.462.98.64 1.539h1.835a7.024 7.024 0 0 0-3.072-2.472c.218.284.418.598.597.933zM10.855 4a7.966 7.966 0 0 0-.468-1.068C9.835 1.897 9.17 1.282 8.5 1.077V4h2.355z"/>
  </svg>
</button>

      </div>
      <div>
        
      </div>
    </nav>
  );
}

export default Navbar;
